import { useState } from "react";
function FormEventoNuevo(){
    const [titulo, setTitulo] = useState("");
    const [lugar, setLugar] = useState("");
    const [deporte, setDeporte] = useState("");
    const [dia, setDia] = useState("");
    const [hora, setHora] = useState("00:00");
    const [cantParticipantes, setCantParticipantes] = useState(0);
    const [estado, setEstado] = useState(0);
    const [id_jugador, setIdJugador] = useState([]);

    // {
    //     _id : ObjectId(), -> Default
    //     titulo: "",
    //     id_jugador: [],
    //     lugar: "",
    //     deporte: "",
    //     estado: 0,  -> Deffault
    //     cantParticipantes: 0,
    //     dia: "",
    //     hora: "00:00",
    //  }

    function handleTitulo(e){
        setTitulo(e.target.value);
    }
    function handleLugar(e){
        setLugar(e.target.value);
    }
    function handleDeporte(e){
        setDeporte(e.target.value);
    }
    function handleDia(e){
        setDia(e.target.value);
    }
    function handleHora(e){
        setHora(e.target.value);
    }
    function handleCantParticipantes(e){
        setCantParticipantes(e.target.value);
    }
    function handleEstado(e){
        setEstado(e.target.value);
    }
    function handleIdJugador(e){
        setIdJugador(e.target.value);
    }
    

    return (
        <div>
            <h1>Evento nuevo</h1>
            <div className="container">
                <form action="" method="post">
                    <div className="mb-3">
                        <label htmlFor="titulo" className="form-label">Título</label>
                        <input type="text" name="titulo" className="form-control" id="titulo" aria-describedby="tituloHelp" />
                        <div id="tituloHelp" className="form-text">Ingresá el nombre del evento.</div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="lugar" className="form-label">Dirección</label>
                        <input type="text" name="lugar" className="form-control" id="lugar" aria-describedby="lugarHelp" />
                        <div id="lugarHelp" className="form-text">Ingresá la dirección del evento.</div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="deporte" className="form-label">Deporte</label>
                        <input type="text" name="deporte" className="form-control" id="deporte" aria-describedby="deporteHelp" />
                        <div id="deporteHelp" className="form-text">Seleccioná el deporte.</div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="deporte" className="form-label">Deporte</label>
                        <input type="text" name="deporte" className="form-control" id="deporte" aria-describedby="deporteHelp" />
                        <div id="deporteHelp" className="form-text">Seleccioná el deporte.</div>
                    </div>
                    <div class="row g-2">
                        <div className="col-mb">
                            <label htmlFor="dia" className="form-label">Día</label>
                            <input type="date" name="dia" className="form-control" id="dia" aria-describedby="diaHelp" />
                        </div>
                        <div className="col-mb">
                            <label htmlFor="hora" className="form-label">Hora</label>
                            <input type="time" name="hora" className="form-control" id="hora" aria-describedby="horaHelp" />
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}
export default FormEventoNuevo;
