import { useState } from "react";
import * as ContactService from "../services/contactos.services";
import Navbar from "../pages/Navbar.page";
import ListContactos from "../components/Contactos/ListContactos.jsx";

function BuscarContactos(){
    const [contactos, setContactos] = useState([]);

    // useEffect(() => {
    //     ContactService.findContactsNew()
    //     .then(contacts => {
    //         setContactos(contacts);
    //     })
    //     .catch(error => {
    //         console.log("error", error);
    //     });
    // }, []);

    function handleSubmit(e){
        e.preventDefault();
        console.log(e);
        ContactService.findContactsNew()
        .then(contacts => {
            setContactos(contacts);
        })
        .catch(error => {
            console.log("error", error);
        });
    }


    return(
        <>
            <Navbar />
            <div className="container">
                <h1>Buscar Contactos</h1>

                <form className="d-flex" role="search" onSubmit={handleSubmit}>
                    <input className="form-control me-2" name="name" type="search" placeholder="IngresÃ¡ un nombre" aria-label="Search" />
                    <button className="btn btn-outline-success" type="submit">Buscar</button>
                </form>
                <div className="row">
                    {contactos.map(contact => (
                        <ListContactos key={contact._id} contacto={contact} buscador={"buscar"} />
                    ))}
                </div>

            </div>
        </>
    )
}

export default BuscarContactos;
