import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import * as EventosServices from "../services/eventos.services";
import * as ParticipantesServices from "../services/participantes.services";
// import Participantes from "../components/Participantes/Participantes";


function EventoView() {
  const { evento_id } = useParams();

  const [evento, setEvento] = useState({
    _id: "",
    titulo: "",
    lugar: "",
    deporte: "",
    estado: 0,
    cantParticipantes: 0,
    dia: "",
    hora: "00:00",
  });

  const [participacionEvento, setParticipacion] = useState({
    _id: "",
    evento_id: "",
    user_id: "",
    estado: 0,
    updated_at: "",
  })
  
  useEffect(() => {
    EventosServices.findByID(evento_id)
      .then((evento) => {
        setEvento(evento)
      })
      .catch((error) => console.log(error));
      
    ParticipantesServices.findByEventId(evento_id, evento.cantParticipantes)
      .then((participation ) => {
        setParticipacion(participation)
        console.log('%cEventoView.jsx line:40 participacionEvento', 'color: #007acc;', participacionEvento);
      })
      .catch((error) => console.log(error));      
  }, [evento_id, evento.cantParticipantes]);

  return (
    <div>
      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">
          <Link className="navbar-brand btn btn-success text-white" to="/">
            Volver
          </Link>
        </div>
      </nav>

      <div className="d-flex align-content-between flex-wrap">
        <div className="container-fluid">
          <div className="container pt-3 cuerpoLista">
            <div className="card border-success mb-3">
              <div className="card-header border-success text-center">
                <h1>{evento.titulo}</h1>
              </div>
              <div className="card-body">
                <div className="col-md-6">
                  <p className="m-0 p-0">Lugar: {evento.lugar}</p>
                  <p className="m-0 p-0">Deporte: {evento.deporte}</p>
                  <p className="m-0 p-0">
                    Fecha del evento: {evento.dia} {evento.hora}
                  </p>
                  <p className="m-0 p-0">
                    Cantidad de Participantes: {evento.cantParticipantes} -{" "}
                    {evento.estado === 0
                      ? "Estado: No se juega"
                      : "Estado: Se juega"}
                  </p>
                </div>
              </div>
            </div>

            {/* <Participantes evento={evento._id} arrayParticipacion={participacionEvento} /> */}
          </div> 

          <div className="container text-center">
            {/* { participacionEvento === 0 ?  (
              <button className="btn btn-success p-3 w-50" type="button" >
                Participar
              </button>
            ) : (
              <button className="btn btn-danger p-3 w-50" type="button" >
                No Participar
              </button>
            )}  */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventoView;
