        "Content-Type": "application/json",
        "auth-token": localStorage.getItem("token"),
      },
      body: JSON.stringify({
        user_id: JSON.parse(localStorage.getItem("user"))._id,
        estado: estado,
      }),
    })
      .then((response) => response.json())
      .catch((error) => console.log(error));
  } else {
    window.location.href = "/login";
  }
}

