import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import * as EventosServices from "../services/eventos.services";
// import { format } from 'date-fns'

function EventoView() {
  const { id } = useParams();
  const [evento, setEvento] = useState({
    _id: "",
    titulo: "",
    id_jugador: [],
    lugar: "",
    deporte: "",
    estado: 0,
    cantParticipantes: 0,
    dia: "",
    hora: "00:00",
  });

  useEffect(() => {
    console.log("Entre al useEffect");
    EventosServices.findByID(id)
      .then((evento) => setEvento(evento))
      .catch((err) => console.log(err));
  }, [id]);

  return (
    <div>
      <nav className="navbar navbar-expand-lg bg-light">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
          <i classNameName="bi bi-chevron-left"></i>Back
          </Link>
        </div>
      </nav>

      <div className="container">
        
        <div classNameName="card">
          <div classNameName="card-header">
            <h1>{evento.titulo}</h1>
          </div>
          <div classNameName="card-body">
            <div classNameName="row">
              <div classNameName="col-md-6">
                <p>Lugar: {evento.lugar}</p>
                <p>Deporte: {evento.deporte}</p>
                <p>
                  Fecha del evento: {evento.dia} {evento.hora}
                </p>
                <p>
                  Cantidad de Participantes: {evento.cantParticipantes} -{" "}
                  {evento.estado === 0
                    ? "Estado: No se juega"
                    : "Estado: Se juega"}
                </p>
              </div>
            </div>
          </div>
        </div>

        <div classNameName="d-grid gap-2 h-25">
          <button className="btn btn-primary" type="submit">
            Participar
          </button>
        </div>
      </div>
    </div>
  );
}

export default EventoView;
