import { useState, useEffect } from "react";
import UserEventosList from "../UserEventosList/UserEventosList";
import * as ParticipantesServices from "../../services/participantes.services";
import * as UserServices from "../../services/user.services";

function Participantes({ players }) {
  // console.log('%cParticipantes.jsx line:7 players', 'color: #007acc;', players);
  const [participantes, setParticipantes] = useState([]);

  useEffect (() => {
    const user_ids = players.map(player => player.user_id); 
    // console.log('%cParticipantes.jsx line:12 user_ids', 'color: #007acc;', user_ids);
    UserServices.getAllPlayers(user_ids)
    .then(users => {
      console.log('%cParticipantes.jsx line:15 users', 'color: #007acc;', users);
      setParticipantes(users);
    })
  }, [players])
  
  return (
    <>
    <div className="card mb-3">
      <div className="card-header">
        <h2>Participantes</h2>
      </div>
      <ol className="list-group list-group-numbered">
        {
          participantes.map(jugador => (
            <UserEventosList key={jugador._id} usuario={jugador}></UserEventosList>
          ))
        }
      </ol>
    </div>
    <div className="container text-center">
      <button className="btn btn-success p-3 w-50" type="button" >
        Participar
      </button>
  </div>
  </>
  );
}

export default Participantes;
