import { useState, useEffect } from "react";
import UserEventosList from "../UserEventosList/UserEventosList";
import * as UserServices from "../../services/user.services";

function Participantes({ players }) {
  console.log('%cParticipantes.jsx line:6 players', 'color: #007acc;', players);
  const [participantes, setParticipantes] = useState([]);
  const [estado, setEstado] = useState(0);

  // Tomo el estado del usuario logueado 
  const user_logged = JSON.parse(localStorage.getItem("user"))._id;
  console.log('%cParticipantes.jsx line:11 user_logged', 'color: #007acc;', user_logged);
  players.forEach(participacion => {
    console.log('%cParticipantes.jsx line:14 participacion', 'color: #007acc;', participacion);
    if(user_logged === participacion.user_id){
      setEstado(participacion.estado);
    }
  });

  useEffect(() => {
    const user_ids = players.map((player) => player.user_id);
    UserServices.getAllPlayers(user_ids)
    .then((users) => {
      setParticipantes(users);
    });
  }, [players]);

  return (
    <>
      <div className="card mb-3">
        <div className="card-header">
          <h2>Participantes</h2>
        </div>
        <ol className="list-group list-group-numbered">
          {participantes.map((jugador) => (
            <UserEventosList key={jugador._id} user={jugador}></UserEventosList>
          ))}
        </ol>
      </div>
      <div className="container text-center">
            {estado === 0 ? (
              <button className="btn btn-success p-3 w-50" type="button">
              Participar
            </button>
            ) : (
              <button className="btn btn-danger p-3 w-50" type="button">
                No Participar
              </button>
            )}
          </div>
    </>
  );
}

export default Participantes;
