import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import * as EventosServices from "../services/eventos.services";
// import { format } from 'date-fns'

function EventoView() {
  const { id } = useParams();
  const [evento, setEvento] = useState({
    _id: "",
    titulo: "",
    id_jugador: [],
    lugar: "",
    deporte: "",
    estado: 0,
    cantParticipantes: 0,
    dia: "",
    hora: "00:00",
  });

  useEffect(() => {
    console.log("Entre al useEffect");
    EventosServices.findByID(id)
      .then((evento) => setEvento(evento))
      .catch((err) => console.log(err));
  }, [ id ]);

  return (
    <div>
      <nav class="navbar navbar-expand fixed-top bg-light">
        <div class="container-fluid">
          <a class="navbar-brand" href="/">Back</a>
        </div>
      </nav>

      <div className="container">
      
      <button className="btn btn-primary" onClick={() => window.history.back()}><i className="bi bi-chevron-left"></i> Volver</button>
      <div className="vh-90">
        <div className="card">
          <div className="card-header">
            <h1>{evento.titulo}</h1>
          </div>
          <div className="card-body">
            <div className="row">
              <div className="col-md-6">
                <div className="form-group">
                  <p>Lugar: {evento.lugar}</p>
                  <p>Deporte: {evento.deporte}</p>
                  <p>Fecha del evento: {evento.dia} {evento.hora}</p>
                  <p>Cantidad de Participantes: {evento.cantParticipantes} - {evento.estado === 0 ? (<p>Estado: No se juega</p>) : (<p>Estado: Se juega</p>)}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      </div>

      <div className="d-grid gap-2 h-25">
        <button className="btn btn-primary" type="submit">Participar</button>
      </div>
      
    </div>
  );
}

export default EventoView;
