import React, { useEffect, useState } from "react";
import { Link, useParams, useNavigate } from "react-router-dom";
import * as EventosServices from "../services/eventos.services";
// import ParticipanteLista from "../components/ParticipantesLista/ParticipantesLista.component";
import Participantes from "../components/Participantes/Participantes";


// import { format } from 'date-fns'

function EventoView() {
  let navigate = useNavigate();

  const { id } = useParams();

  const [evento, setEvento] = useState({
    _id: "",
    titulo: "",
    id_jugador: [],
    lugar: "",
    deporte: "",
    estado: 0,
    cantParticipantes: 0,
    dia: "",
    hora: "00:00",
  });

  const [participacion, setParticipacion] = useState();
  
  useEffect(() => {
    EventosServices.findByID(id)
      .then((evento) => {
        evento.id_jugador.forEach(j => {
          if ( j.idJ === JSON.parse(localStorage.getItem('user'))._id ) {
            setParticipacion(j.estado)
          }
        });

        setEvento(evento)
      })
      .catch((err) => console.log(err));
  }, [id, participacion]);

  function participar(){
    EventosServices.participar(evento._id, JSON.parse(localStorage.getItem('user'))._id)
    .then(res => {
      window.location.reload();
    })
  }

  function noParticipar(){
    EventosServices.noParticipar(evento._id, JSON.parse(localStorage.getItem('user'))._id)
    .then(res => {
      window.location.reload();
    })
  }


  return (
    <div>
      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">
          <Link className="navbar-brand btn btn-success text-white" to="/">
            Volver
          </Link>
        </div>
      </nav>

      <div className="d-flex align-content-between flex-wrap">
        <div className="container-fluid">
          <div className="container pt-3 cuerpoLista">
            <div className="card border-success mb-3">
              <div className="card-header border-success text-center">
                <h1>{evento.titulo}</h1>
              </div>
              <div className="card-body">
                <div className="col-md-6">
                  <p className="m-0 p-0">Lugar: {evento.lugar}</p>
                  <p className="m-0 p-0">Deporte: {evento.deporte}</p>
                  <p className="m-0 p-0">
                    Fecha del evento: {evento.dia} {evento.hora}
                  </p>
                  <p className="m-0 p-0">
                    Cantidad de Participantes: {evento.cantParticipantes} -{" "}
                    {evento.estado === 0
                      ? "Estado: No se juega"
                      : "Estado: Se juega"}
                  </p>
                </div>
              </div>
            </div>

            <Participantes evento={evento._id} />
          </div> 

          <div className="container text-center">
            { participacion === 0 ?  (
              <button className="btn btn-success p-3 w-50" type="button" onClick={participar}>
                Participar
              </button>
            ) : (
              <button className="btn btn-danger p-3 w-50" type="button" onClick={noParticipar}>
                No Participar
              </button>
            )} 
          </div>
        </div>
      </div>
    </div>
  );
}

export default EventoView;
