import { useState, useEffect } from "react";
import * as ParticipantesServices from "../../services/participantes.services";

import ParticipanteLista from "../../components/ParticipantesLista/ParticipantesLista.component";

function Participantes({ evento, arrayParticipacion, cantidad }) {
  const [participantes, setParticipantes] = useState([]);

  useEffect (() => {
    if(cantidad && evento && arrayParticipacion){
      ParticipantesServices.findByEventId(evento, arrayParticipacion, cantidad)
      .then((p) => {
        // console.log('%cParticipantes.jsx line:16 p', 'color: #007acc;', p);
        setParticipantes(p);
      })
      .catch((err) => console.log(err));
    }
  }, [evento, arrayParticipacion, cantidad]);

  return (
    <div className="card mb-3">
      <div className="card-header">
        <h2>Participantes</h2>
      </div>
      <ol className="list-group list-group-numbered">
        {participantes ? (
          participantes.map((user) => (
            <ParticipanteLista key={user._id} usuario={user} />
          ))
        ) : (
          <li className="p-2 text-center">No hay participantes</li>
        )}
      </ol>
    </div>
  );
}

export default Participantes;
